mapper.update:
update article
user_id = #{userId}, article_category = #{articleCategory}, article_cover = #{articleCover}, article_title = #{articleTitle}, article_content = #{articleContent}, article_type = #{articleType}, original_url = #{originalUrl}, article_like = #{articleLike}, article_view = #{articleView}, article_collect = #{articleCollect}, is_top = #{isTop}, is_delete = #{isDelete}, is_ok = #{isOk}, status = #{status}, create_time = #{createTime}, update_time = #{updateTime}, create_by = #{createBy}, update_by = #{updateBy}, judge_by = #{judgeBy}, judge_time = #{judgeTime},
where article_id = #{articleId}

update article
set  article_view = article_view + 1
where article_id = #{articleId};
